const menuToggle=document.getElementById("menu-toggle"),nav=document.getElementById("nav");menuToggle.addEventListener("click",()=>nav.classList.toggle("show"));const observer=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&e.target.classList.add("visible")})},{threshold:.2});document.querySelectorAll(".hero-text,.hero img,.benefit-card,.service-card,.testimonial").forEach(e=>observer.observe(e));let index=0,slides=document.querySelectorAll(".slide"),slider=document.getElementById("slider");setInterval(()=>{index=(index+1)%slides.length,slider.style.transform=`translateX(-${100*index}%)`},4e3);
